import Link from 'next/link';
import { withAmp } from 'next/amp'
import Head from 'next/head';

export const config = { amp: true };

const linkStyle = {
  marginRight: 15,
  'text-decoration': 'none',
  color: 'purple'
};

 function getPages() {
   return [
     { id: '/resume', title: 'Resume' },
     { id: '/posts', title: 'Posts' },
     { id: '/talks', title: 'Talks' }
   ];
 }

function Header() {
  return (
    <div>
      <Head>
        <title>Naina Raisinghani</title>
        <script async key="amp-sidebar" custom-element="amp-sidebar" src="https://cdn.ampproject.org/v0/amp-sidebar-0.1.js" />
      </Head>
      <header>
        <style jsx>{`
            @font-face {
                font-family: 'Bai Jamjuree';
                font-style: sans-serif;
                src: url('https://fonts.googleapis.com/css?family=Bai+Jamjuree');
            }
            header {
                font-family: 'Bai Jamjuree', sans-serif;
                width: 100%;
                background-color: #07c1b98a;
                padding: 1vh 0 1vh 0;
                height: 3em;
            }
            .name {
              position: fixed;
              text-align:center;
              left: 50%;
              transform: translateX(-50%);
              font-size: 2em;
              top: 0.5em;
            }
            .links {
              position: relative; 
              top: 1em;
              padding-left: 1em;
            }
            amp-sidebar, #toggleSidebar {
              display: none;
            }

            @media only screen and (max-width: 768px) {
              amp-sidebar, #toggleSidebar {
                display: block;
                position: relative; 
                padding-left: 1em;
                height: 100%
              }
              #toggleSidebar {
                padding-top: 1em;
              }
              .links {
                display: none;
              }
            }
        `}</style>
